@media all {
    .print-format {
        /*216mm or 21.6cm or 8.5in (inches)*/
        max-width: 21.6cm;
        width: 21.6cm;
        /*279mm or 27.9cm or 11.0in (inches)*/
        min-height: 27.9cm;
        height: 27.9cm;
    }
    .print-format.landscape {
        max-width: 27.9cm;
        width: 27.9cm;
    }
    .print-format.portrait {
        max-width: 21.6cm;
        width: 21.6cm;
    }
}


.headerCSS{
    font-size: 1em;
	border-collapse: collapse;
    font-family: Arial, Helvetica, sans-serif;
    text-transform: uppercase;
}

.headerCSS2{
    font-size: 0.8em;
    font-family: Arial, Helvetica, sans-serif;
    margin-bottom: 20px;
}

